tamp int) string {\\u003cbr\\u003e    tm := time.Unix(int64(timestamp), 0)\\u003cbr\\u003e    return tm.Format(\\\"2006-01-02 15:04\\\")\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//时间戳转年月日\\u003cbr\\u003efunc DateFormatYmd(timestamp int) string {\\u003cbr\\u003e    tm := time.Unix(int64(timestamp), 0)\\u003cbr\\u003e    return tm.Format(\\\"2006-01-02\\\")\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//获取当前年月\\u003cbr\\u003efunc DateYmFormat() string {\\u003cbr\\u003e    tm := time.Now()\\u003cbr\\u003e    return tm.Format(\\\"2006-01\\\")\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//获取年月日时分秒（字符串类型）\\u003cbr\\u003efunc DateNowFormatStr() string {\\u003cbr\\u003e    tm := time.Now()\\u003cbr\\u003e    return tm.Format(\\\"2006-01-02 15:04:05\\\")\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//时间戳\\u003cbr\\u003efunc DateUnix() int {\\u003cbr\\u003e    t := time.Now().Local().Unix()\\u003cbr\\u003e    return int(t)\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//获取年月日时分秒(time类型)\\u003cbr\\u003efunc DateNowFormat() time.Time {\\u003cbr\\u003e    tm := time.Now()\\u003cbr\\u003e    return tm\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//获取第几周\\u003cbr\\u003efunc DateWeek() int {\\u003cbr\\u003e    _, week := time.Now().ISOWeek()\\u003cbr\\u003e    return week\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//获取年、月、日\\u003cbr\\u003efunc DateYMD() (int,int, int) {\\u003cbr\\u003e    year, month, day := DateYmdInts()\\u003cbr\\u003e    return year,month,day\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e// 获取年月日\\u003cbr\\u003efunc DateYmdFormat() string {\\u003cbr\\u003e    tm := time.Now()\\u003cbr\\u003e    return tm.Format(\\\"2006-01-02\\\")\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e// 获取日期的年月日\\u003cbr\\u003efunc DateYmdInts() (int, int, int) {\\u003cbr\\u003e    timeNow := time.Now()\\u003cbr\\u003e    year, month, day := timeNow.Date()\\u003cbr\\u003e    return year, int(month), day\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003epackage main\\u003cbr\\u003e\\u003cbr\\u003eimport (\\u003cbr\\u003e\\t\\\"fmt\\\"\\u003cbr\\u003e\\t\\\"time\\\"\\u003cbr\\u003e)\\u003cbr\\u003e\\u003cbr\\u003efunc main() {\\u003cbr\\u003e\\u003cbr\\u003e\\tsleepDemo()\\u003cbr\\u003e}\\u003cbr\\u003efunc test1() {\\u003cbr\\u003e\\u003cbr\\u003e\\tt := time.Now() //\\t获取当前的时间对象\\u003cbr\\u003e\\t//t type:time.Time value:time.Time{wall:0xbf96f99387cfb7c3, ext:190223, loc:(*time.Location)(0x571b80)}\\u003cbr\\u003e\\tfmt.Printf(\\\"t type:%T value:%#v \\\\n\\\", t, t)\\u003cbr\\u003e\\u003cbr\\u003e\\t//比较时间戳\\u003cbr\\u003e\\tdataNumber1 := int64(1586252136)\\u003cbr\\u003e\\t// dataNumber1 := endTime22\\u003cbr\\u003e\\tif t.Unix() \\u0026gt; dataNumber1 {\\u003cbr\\u003e\\t\\tfmt.Println(\\\"111111\\\")\\u003cbr\\u003e\\t}\\u003cbr\\u003e\\tfmt.Println(\\\"22222222\\\")\\u003cbr\\u003e\\u003cbr\\u003e}\\u003cbr\\u003e\\u003cbr\\u003e//时间类型\\u003cbr\\u003efunc timeDemo() {\\u003cbr\\u003e\\tnow := time.Now()\\u003cbr\\u003e\\u003cbr\\u003e\\tfmt.Println(\\\"now time is \\\", now) //\\tnow time is  2020-03-25 22:56:17.263142275 +0800 CST m=+0.000158437\\u003cbr\\u003e\\u003cbr\\u003e\\tyear := now.Year()\\u003cbr\\u003e\\tfmt.Println(\\\"year:\\\", year) //year: 2020\\u003cbr\\u003e\\u003cbr\\u003e\\tmonth := now.Month()\\u003cbr\\u003e\\tfmt.Printf(\\\"month:%02d\\\\n\\\", month) //month:03\\u003cbr\\u003e\\u003cbr\\u003e\\tday := now.Day()\\u003cbr\\u003e\\tfmt.Println(\\\"day:\\\", day) //day: 25\\u003cbr\\u003e\\u003cbr\\u003e\\thour := now.Hour()\\u003cbr\\u003e\\tfmt.Println(\\\"hour:\\\", hour) //hour: 22\\u003cbr\\u003e\\u003cbr\\u003e\\tminute := now.Minute()\\u003cbr\\u003e\\tfmt.Println(\\\"minute:\\\", minute) //minute: 56\\u003cbr\\u003e\\u003cbr\\u003e\\tsecond := now.Second()\\u003cbr\\u003e\\tfmt.Println(\\\"second:\\\", second) //\\tsecond: 17\\u003cbr\\u003e\\u003cbr\\u003e\\t// 2020-03-25 22:56:17\\u003cbr\\u003e\\tfmt.Printf(\\\"%d-%02d-%02d %02d:%02d:%02d\\\\n\\\", year, month, day, hour, minute, 