INSERT INTO `goadmin_operation_log` VALUES (855, 1, '/admin/new/article', 'POST', '127.0.0.1', '{\"__go_admin_previous_\":[\"/admin/info/article?__page=1\\u0026__pageSize=10\\u0026__sort=id\\u0026__sort_type=desc\"],\"__go_admin_t_\":[\"0a7530e2-11f8-4a43-b5fb-6c66f3400f9d\"],\"article_status\":[\"1\"],\"auth\":[\"kiki\"],\"category_id\":[\"1\"],\"content\":[\"\\u003cp\\u003e\\u003cspan style=\\\"font-family: 宋体; font-weight: bold;\\\"\\u003eCtrl+D 复制行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+X 删除行\\u0026lt;br\\u0026gt;\\u003cbr\\u003e快速修复 alt+enter (modify/cast)\\u0026lt;br\\u0026gt;\\u003cbr\\u003e代码提示 alt+/\\u0026lt;br\\u0026gt;\\u003cbr\\u003ectr+G 定位某一行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eShift+F6 重构-重命名\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+R 替换文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+F 查找文本\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;Ctrl+E 最近打开的文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+J 自动代码\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;组织导入 ctr+alt+O\\u0026lt;br\\u0026gt;\\u003cbr\\u003e格式化代码 ctr+alt+L\\u0026lt;br\\u0026gt;\\u003cbr\\u003e大小写转化 ctr+shift+U\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;hr\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;###goland 常用快捷键列表\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;Alt+回车 导入包,自动修正\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+N   查找类\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Shift+N 查找文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Alt+L  格式化代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Alt+O 优化导入的类和包\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+Insert 生成代码(如get,set方法,构造函数等)\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+E或者Alt+Shift+C  最近更改的代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+R 替换文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+F 查找文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Shift+Space 自动补全代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+空格 代码提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Alt+Space 类名或接口名提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+P 方法参数提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Shift+Alt+N 查找类中的方法或变量\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+Shift+C 对比最近修改的代码\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;Shift+F6  重构-重命名\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Shift+先上键\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+X 删除行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+D 复制行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+/ 或 Ctrl+Shift+/  注释（// 或者/\\u0026lt;em\\u0026gt;…\\u0026lt;/em\\u0026gt;/ ）\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+J  自动代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+E 最近打开的文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+H 显示类结构图\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Q 显示注释文档\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+F1 查找代码所在位置\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+1 快速打开或隐藏工程面板\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Alt+ left/right 返回至上次浏览的位置\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+ left/right 切换代码视图\\u0026lt;br\\u0026gt;\\u003cbr\\u003eAlt+ Up/Down 在方法间快速移动定位\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Shift+Up/Down 代码向上/下移动。\\u0026lt;br\\u0026gt;\\u003cbr\\u003eF2 或Shift+F2 高亮错误或警告快速定位\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;代码标签输入完成后，按Tab，生成代码。\\u0026lt;br\\u0026gt;\\u003cbr\\u003e选中文本，按Ctrl+Shift+F7 ，高亮显示所有该文本，按Esc高亮消失。\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+W 选中代码，连续按会有其他效果\\u0026lt;br\\u0026gt;\\u003cbr\\u003e选中文本，按Alt+F3 ，逐个往下查找相同文本，并高亮显示。\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+Up/Down 光标跳转到第一行或最后一行下\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCtrl+B 快速打开光标处的类或方法\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;##1. 查询快捷键\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+N   查找类\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+N  查找文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+ALT+N 查找类中的方法或变量\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCIRL+B   找变量的来源\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+B  找所有的子类\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+B  找变量的类\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+G   定位行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+F   在当前窗口查找文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+F  在指定窗口查找文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+R   在 当前窗口替换文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+R  在指定窗口替换文本\\u0026lt;br\\u0026gt;\\u003cbr\\u003eALT+SHIFT+C  查找修改的文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+E   最近打开的文件\\u0026lt;br\\u0026gt;\\u003cbr\\u003eF3   向下查找关键字出现位置\\u0026lt;br\\u0026gt;\\u003cbr\\u003eSHIFT+F3  向上一个关键字出现位置\\u0026lt;br\\u0026gt;\\u003cbr\\u003eF4   查找变量来源\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+F7  选中的字符查找工程出现的地方\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+O  弹出显示查找内容\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;##2. 自动代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eALT+回车  导入包,自动修正\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+L  格式化代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+I  自动缩进\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+O  优化导入的类和包\\u0026lt;br\\u0026gt;\\u003cbr\\u003eALT+INSERT  生成代码(如GET,SET方法,构造函数等)\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+E 最近更改的代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+SPACE 自动补全代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+空格  代码提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+SPACE  类名或接口名提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+P   方法参数提示\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+J   自动代码\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+T  把选中的代码放在 TRY{} IF{} ELSE{} 里\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;##3. 复制快捷方式\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+D   复制行\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+X   剪切,删除行\\u0026lt;/p\\u0026gt;\\u003cbr\\u003e\\u0026lt;p\\u0026gt;##4. 其他快捷方式\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCIRL+U   大小写切换\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+Z   倒退\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+SHIFT+Z  向前\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+ALT+F12  资源管理器打开文件夹\\u0026lt;br\\u0026gt;\\u003cbr\\u003eALT+F1   查找文件所在目录位置\\u0026lt;br\\u0026gt;\\u003cbr\\u003eSHIFT+ALT+INSERT 竖编辑模式\\u0026lt;br\\u0026gt;\\u003cbr\\u003eCTRL+/   注释//\\u0026lt;br\\u0026gt;\\u0