INSERT INTO `goadmin_operation_log` VALUES (1052, 1, '/admin/edit/article', 'POST', '127.0.0.1', '{\"__go_admin_previous_\":[\"/admin/info/article?__page=1\\u0026__pageSize=10\\u0026__sort=id\\u0026__sort_type=desc\"],\"__go_admin_t_\":[\"1e5145f9-d139-48b9-890a-70b754222bfe\"],\"article_status\":[\"1\"],\"auth\":[\"kiki\"],\"category_id\":[\"1\"],\"content\":[\"一：首先贴代码（你们大概看看就行，在看看后面的数据 你们就能理解 结构体嵌套的案例）       如果是PHP就方便很多，直接数组嵌套数组就行，也就是二维数组。```bashpackage modelimport (\\t\\\"fmt\\\"\\t\\\"time\\\")type Find struct {\\tArticle       []Article\\tSelectArticle []SelectArticle\\tRecoArticle   []SelectArticle\\tSelectLabel   []SelectLabel\\tIndexAbout    SiteInfo\\tIndexContact  SiteInfo}type Article struct {\\tId          int       `gorm:\\\"column:id\\\"`\\tTitle       string    `gorm:\\\"column:title\\\"`\\tAuth        string    `gorm:\\\"column:auth\\\"`\\tImage       string    `gorm:\\\"column:image\\\"`\\tInfo        string    `gorm:\\\"column:info\\\"`\\tLabel       string    `gorm:\\\"column:label\\\"`\\tContent     string    `gorm:\\\"column:content\\\"`\\tCategory_id int       `gorm:\\\"column:category_id\\\"`\\tCreate_at   time.Time `gorm:\\\"column:create_at\\\"`}type SelectArticle struct {\\tId    int    `gorm:\\\"column:id\\\"`\\tTitle string `gorm:\\\"column:title\\\"`}type SelectLabel struct {\\tId         int    `gorm:\\\"column:id\\\"`\\tLabel_name string `gorm:\\\"column:label_name\\\"`}type SiteInfo struct {\\tPersonal_introduction string `gorm:\\\"column:personal_introduction\\\"`\\tContact               string `gorm:\\\"column:contact\\\"`\\tAbout                 string `gorm:\\\"column:about\\\"`}//返回首页数据//article:文章主体数据、 selectArticle:文章精选、 recoArticle:文章推荐、selectLabel文章标签、indexAbout:关于、indexContact：联系func IndexArticle() (result Find) {\\tvar (\\t\\tarticle       []Article\\t\\tselectArticle []SelectArticle\\t\\trecoArticle   []SelectArticle\\t\\tselectLabel   []SelectLabel\\t\\tindexAbout    SiteInfo\\t\\tindexContact  SiteInfo\\t)\\tarticle = make([]Article, 100)\\tselectArticle = make([]SelectArticle, 100)\\trecoArticle = make([]SelectArticle, 100)\\tselectLabel = make([]SelectLabel, 100)\\tG_db.Debug().Table(\\\"article\\\").Select(\\\"id,title,auth,image,info\\\").Where(\\\"article_status = \'1\'\\\").Limit(3).Order(\\\"create_at desc\\\").Scan(\\u0026article)\\tG_db.Debug().Table(\\\"article\\\").Select(\\\"id,title\\\").Where(\\\"selected_articles_status = \'1\' and article_status = \'1\'\\\").Limit(6).Order(\\\"create_at desc\\\").Scan(\\u0026selectArticle)\\tG_db.Debug().Table(\\\"article\\\").Select(\\\"id,title\\\").Where(\\\"selected_articles_status = \'1\' and article_status = \'1\'\\\").Limit(6).Order(\\\"create_at desc\\\").Scan(\\u0026recoArticle)\\tG_db.Debug().Table(\\\"article_label\\\").Select(\\\"id,label_name\\\").Order(\\\"id desc\\\").Scan(\\u0026selectLabel)\\tG_db.Debug().Table(\\\"users\\\").Select(\\\"about\\\").Scan(\\u0026indexAbout)\\tG_db.Debug().Table(\\\"users\\\").Select(\\\"contact\\\").Scan(\\u0026indexContact)\\tresult = Find{\\t\\tArticle:       article,\\t\\tSelectArticle: selectArticle,\\t\\tRecoArticle:   recoArticle,\\t\\tSelectLabel:   selectLabel,\\t\\tIndexAbout:    indexAbout,\\t\\tIndexContact:  indexContact,\\t}\\tfor _, v := range result.Article {\\t\\tfmt.Printf(\\\"%v,%v\\\\n\\\", v.Id, v.Title)\\t}\\tfor _, v := range result.SelectArticle {\\t\\tfmt.Printf(\\\"%v,%v\\\\n\\\", v.Id, v.Title)\\t}\\treturn}```\"],\"create_at\":[\"2020-04-27 00:00:00\"],\"id\":[\"28\"],\"image\":[\"9f1caeeb-8c52-4053-bcd1-b89f62e2d74d.jpeg\"],\"image__delete_flag\":[\"0\"],\"info\":[\"golang该如何循环 多层结构体\"],\"label[]\":[\"3\"],\"selected_articles_status\":[\"1\"],\"title\":[\"golang循环多层结构体\"],\"update_at\":[\"2020-04-26 00:00:00\"]}', '2020-04-28 22:57:37', '2020-04-28 22:57:37');
